import React from 'react'
import Table from 'react-bootstrap/Table'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import Cookie from 'js-cookie'
import {
  getPackages
} from '../../reducers/customer'
// import { push } from 'connected-react-router'



class CustomerHome extends React.Component {

  componentDidMount() {
    const id = Cookie.get('id')
    this.props.getPackages(id)
  }

  makeTR = (p, id) => {
    return (
      <React.Fragment key={id}>
        <tr>
          <td> { p.id } </td>
          <td> { p.senderEmail } </td>
          <td> { p.recipientEmail } </td>
          <td> { p.senderAddress } </td>
          <td> { p.recipientAddress } </td>
          <td> { p.deliveryStatus } </td>
          <td> { p.packageWeight } </td>
        </tr>
      </React.Fragment>)
  };

  render() {
    return <div style={divStyle}>
      <h1> Welcome! </h1>
    <Table style={tableStyle} striped bordered hover>
    <thead>
      <tr>
        <th>#</th>
        <th>Sender Email</th>
        <th>Recipient Email</th>
        <th>Sender Address</th>
        <th>Recipient Address</th>
        <th>Status Address</th>
        <th>Weight</th>
      </tr>
    </thead>
    <tbody>
    { this.props.packages.map((pack) => { return this.makeTR.bind(this)(pack, pack.id) }) }
    </tbody>
    </Table>
</div>
    }
}

const tableStyle = {
    margin: '0 auto',
    marginTop: '3%',
};

const divStyle = {
    textAlign: "center",
};

const mapStateToProps = ({ customer }) => ({
  packages: customer.packages,
});

const mapDispatchToProps = dispatch =>
  bindActionCreators(
    {
      getPackages,
    },
    dispatch
  );

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(CustomerHome)
