import React from 'react'
import Table from 'react-bootstrap/Table'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import Cookie from 'js-cookie'
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'
import {
  getPackages,
  changeNewPackageView
} from '../../reducers/customer'
import { push } from 'connected-react-router'



class CustomerHome extends React.Component {
  constructor(props) {
    super(props)
    this.openNewPackage = this.openNewPackage.bind(this)
    this.makeTR = this.makeTR.bind(this)
  }

  componentDidMount() {
    const id = Cookie.get('id')
    this.props.getPackages(id)
  }

  openNewPackage = (status) => {
    this.props.changeNewPackageView(status)
  };

  makeTR = (p, id) => {
    return (
      <React.Fragment key={id}>
        <tr>
          <td> { p.id } </td>
          <td> { p.senderEmail } </td>
          <td> { p.recipientEmail } </td>
          <td> { p.senderAddress } </td>
          <td> { p.recipientAddress } </td>
          <td> { p.deliveryStatus } </td>
          <td> { p.packageWeight } </td>
        </tr>
      </React.Fragment>)
  };

  render() {
    return <div style={divStyle}>
      <div style={{ marginTop: '3%' }}>
        <Button variant='success' onClick={this.openNewPackage(true)}>Ship a new package</Button>
      </div>
      <Modal show={this.props.newPackageViewStatus} onHide={this.openNewPackage(false)}>
        
      </Modal>





      <h1> Your packages:  </h1>
    <Table style={tableStyle} striped bordered hover>
    <thead>
      <tr>
        <th>#</th>
        <th>Sender Email</th>
        <th>Recipient Email</th>
        <th>Sender Address</th>
        <th>Recipient Address</th>
        <th>Status Address</th>
        <th>Weight</th>
      </tr>
    </thead>
    <tbody>
    { this.props.packages.map((pack) => { return this.makeTR(pack, pack.id) }) }
    </tbody>
    </Table>
</div>
    }
}

const tableStyle = {
    margin: '0 auto',
    marginTop: '3%',
};

const divStyle = {
    textAlign: "center",
};

const mapStateToProps = ({ customer }) => ({
  packages: customer.packages,
  newPackageViewStatus: customer.newPackageViewStatus,
});

const mapDispatchToProps = dispatch =>
  bindActionCreators(
    {
      getPackages,
      changeNewPackageView,
    },
    dispatch
  );

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(CustomerHome)
