import React from 'react'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import Col from 'react-bootstrap/Col'
import Card from 'react-bootstrap/Card'
import Table from 'react-bootstrap/Table'
import { List } from 'immutable'
import { apiPost } from '../../utils/api'
import Alert from 'react-bootstrap/Alert';


class Tracking extends React.Component {
  constructor(props) {
    super(props);
    console.log(props.location);
    this.state = {
      notification: { is: false, message: '', type: '', header: '' },
      packageID: props.location.hash ? props.location.hash.substr(1) : null,
      history: List([]),
      showHistory: !!props.location.hash,
    };

    this.makeTR = this.makeTR.bind(this);
    this.getHistory = this.getHistory.bind(this);
  }

  componentDidMount() {
    if (this.state.showHistory) {
      this.getHistory()
    }
  }

  getHistory = () => {
    if (this.state.packageID) {
      apiPost('/tracking')
    }
  };

  makeTR = (p, id) => {
    return (
      <React.Fragment key={id}>
        <tr>
          { this.state.type === 'Delivery' ? <td> <Button name='deliver' onClick={this.handleDeliverButton} id={p.id} variant='success' >Deliver</Button></td> : null}
          <td> { p.id } </td>
          <td> { p.senderEmail } </td>
          <td> { p.recipientEmail } </td>
          <td> { p.senderAddress } </td>
          <td> { p.recipientAddress } </td>
          <td> { p.deliveryStatus } </td>
          <td> { p.packageWeight } </td>
        </tr>
      </React.Fragment>)
  };


  render() {
    return (
      <div>
        <Card className="text-center">
        <Card.Header>Package Tracking</Card.Header>
        <Card.Body>
          <Card.Title>Package History</Card.Title>
          { this.state.showHistory ?
            <Table>
            </Table>
            : <h1>Enter package id</h1> }
        </Card.Body>
      </Card>
      </div>
    )
    }
}

const ControlButtonStyle = {
  marginRight: '16px',
};

const tableStyle = {
  margin: '0 auto',
  textAlign: "center",
};

const h3Style = {
  marginTop: '2.5%',
};

export default Tracking;
