import React from 'react'
import {connect} from "react-redux"
import { bindActionCreators } from 'redux'
import { setEmployeeInfo } from '../../reducers/manager'
import { isManager } from '../../utils/auth'
import Cookie from 'js-cookie'
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'
import Form from 'react-bootstrap/Form'
import Col from 'react-bootstrap/Col'
import Card from 'react-bootstrap/Card'
import Table from 'react-bootstrap/Table'
import { getEmployees, updateEmp, changeNewEmpView } from '../../reducers/manager'


class Manager extends React.Component {
  constructor(props) {
    super(props);
    this.props.setEmployeeInfo({
      id: Cookie.get('id'),
      isManager: isManager(),
      facilityID: Cookie.get('id')
    });
    this.makeTR = this.makeTR.bind(this);
    this.handleNewEmp = this.handleNewEmp.bind(this);
    this.handleChangeNewEmpView = this.handleChangeNewEmpView.bind(this);
    }

  componentDidMount() {
      this.props.getEmployees({
        managerID: this.props.id,
        facilityID: this.props.facilityID,
      });
    }

  makeTR = (e, id) => {
      return (
        <React.Fragment key={id}>
          <tr>
            <td> { e.id } </td>
            <td> { e.firstName } </td>
            <td> { e.lastName } </td>
            <td> { e.position } </td>
            <td> { e.workPhoneNum } </td>
            <td> { e.workEmail } </td>
            <td> { e.salary } </td>
          </tr>
        </React.Fragment>)
  };

  handleNewEmp = (event) => {
    console.log(event)
    //this.props.updateEmp({ ...load, ...this.props.newEmp })
  };

  handleChangeNewEmpView = () => {
    this.props.changeNewEmpView(this.props.showNewEmp)
  };

  render() {
    return (
      <div>
        <div>
          <Card className="text-center">
            <Card.Header>Control Center</Card.Header>
            <Card.Body>
              <Card.Title>Managerial Actions</Card.Title>
              <Button size='lg' variant="success" style={ControlButtonStyle} onClick={this.handleChangeNewEmpView}>Add Employee</Button>
              <Button size='lg' variant="info" style={ControlButtonStyle}>Review Facility Report</Button>
            </Card.Body>
          </Card>
        </div>

        <Modal show={this.props.showNewEmp} onHide={this.handleChangeNewEmpView} name='CreateEmp'>
          <Modal.Header closeButton>
          <Modal.Title>Add New Employee</Modal.Title>
          </Modal.Header>
          <Form.Row>
            <Form.Group as={Col} controlId='formGridEmail'>
              <Form.Label> First Name </Form.Label>
              <Form.Control placeholder='John' name='firstName' value={this.props.newEmp.firstName} onChange={this.handleNewEmp} />
            </Form.Group>
            <Form.Group as={Col} controlId='formGridAddress'>
              <Form.Label> Last Name </Form.Label>
              <Form.Control placeholder='Smith' value={this.props.newEmp.lastName} onChange={this.handleNewEmp} />
            </Form.Group>
          </Form.Row>
        </Modal>






        <h3>Employees</h3>
        <Table style={tableStyle} striped bordered hover>
          <thead>
            <tr>
              <th>#</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Position</th>
              <th>Work Phone</th>
              <th>Work Email</th>
              <th>Salary</th>
            </tr>
          </thead>
          <tbody>
          { this.props.employees.map((e) => { return this.makeTR(e, e.id) }) }
          </tbody>
    </Table>
      </div>
    )
    }
}

const tableStyle = {
  margin: '0 auto',
  textAlign: "center",
};

const ControlButtonStyle = {
  marginRight: '16px',
};

const mapStateToProps = ({ manager }) => ({
  isManager: manager.isManager,
  id: manager.id,
  facilityID: manager.facilityID,
  employees: manager.employees,
  newEmp: manager.newEmp,
  showNewEmp: manager.showNewEmp,
});

const mapDispatchToProps = dispatch =>
  bindActionCreators(
    {
      setEmployeeInfo,
      getEmployees,
      updateEmp,
      changeNewEmpView,
    },
    dispatch
  );

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Manager)
